mapper.select:

select bno, title, content, writer, regdate, updatedate
from (
	select /*+INDEX_DESC(comm_loard pk_board) */ rownum rn, bno, title, content, writer, regdate, updatedate
	from comm_board
	where rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum}-1) * #{amount} ORDER BY BNO DESC
